	function sf_woo_remove_reviews_tab($tabs) {
		
		global $sf_options;
		$product_reviews_pos = "default";
		if ( isset( $sf_options['product_reviews_pos'] ) ) {
		$product_reviews_pos = $sf_options['product_reviews_pos'];
		}
		
		if ( $product_reviews_pos == "default" ) {
